const title = 'AI MVP Development';
const description = `I am seeking a talented developer with expertise in MVP development, AI chatbot development, and API integrations to join a my startup in the AI-driven travel sector. We are considering the use of Bubble or similar no-code/low-code platforms to expedite development without compromising quality.

We are working with a limited budget initially, but this position offers substantial growth potential. The successful candidate will work closely with me to launch our first product. As we gain traction and secure further investment, there is a significant opportunity for you to advance to a leadership role, potentially becoming the Lead Developer or CTO.

This is more than a job—it's a chance to shape the future of travel with cutting-edge technology. If you're ready to take on this challenge and innovate in the dynamic intersection of tech and travel, let's discuss how we can make a global impact together.

Effective communication is essential for this project. Therefore, we prefer candidates within three hours of Central European Time (CET) to ensure smooth collaboration. If you meet this criterion, we'd love to hear from you.`

const payload = {
    title,
    description,
    date: new Date(),
}
msg.payload = payload;
return msg;

try {
    const detectLanguage = global.get('detectLanguage');

    msg.payload.language = detectLanguage(msg.payload.descriptionText);
    msg.payload.languageDeteced = true;
} catch (e) {
    msg.payload.error = e.message;
}

return msg;

if (msg.payload.error) {
    return [null, null, msg];
}

if (msg.payload.documentCount > 0) {
    return null
}
if (msg.payload.languageDeteced && 
    msg.payload.keywordsExtracted && 
    msg.payload.formated && 
    msg.payload.linkedInPostCreated &&
    msg.payload.documentCount === 0) 
{
    return [null,msg];
}

if (msg.payload.documentCount === undefined) {
    msg.routingKey = 'deduplication_check';
    return [msg, null];
}
if (!msg.payload.formated) {
    msg.routingKey = 'format';
    return [msg, null];
}

if (!msg.payload.languageDeteced) {
    msg.routingKey = 'language_detection';
    return [msg, null];
}

if (!msg.payload.keywordsExtracted) {
    msg.routingKey = 'extract_keywords';
    return [msg, null];
}

if (!msg.payload.linkedInPostCreated) {
    msg.routingKey = 'linkedin_post'
    return [msg, null];
}

const generateTags = global.get('generateTags')
try {
    msg.payload.keywords = generateTags(msg.payload.descriptionText);
    msg.payload.keywordsExtracted = true;
} catch (e) {
    msg.payload.error = e.message;
}

return msg;

function htmlToText(html) {
  return html.replace(/<[^>]*>/g, '');
}

try {
  msg.payload.descriptionText = htmlToText(msg.payload.description);
  msg.payload.formated = true;
} catch (e) {
    msg.payload.error = e.message;
}

return msg;

const linkedInPostPayload = msg.payload;
msg.payload = msg.tempPayload;
msg.payload.linkedInPostCreated = true
msg.payload.linkedInPost = linkedInPostPayload.content;
